function addToList(list, newEntity) {
    if (!list.includes(newEntity)) {
        list.push(newEntity);
    }
    return list;
}

    //“vendorReadOnly”, “vendorNativeType”, or "vendorRequired", "type", "vendorPath"
    function addFieldsToList(propertiesArray, prefix) {
        let array = [];
        for (let i = 0; i < propertiesArray.length; i++) {
            let tmp = {};
            let property = propertiesArray[i];
            if (property["@Name"]) {
                tmp.vendorPath = prefix + property["@Name"];
            }
            if (property["@Type"]) {
                tmp = addSwaggerType(property["@Type"], tmp);
                let native = property["@Type"];
                if (property["@c4c:value-help"]) {
                	native += " - " + property["@c4c:value-help"];
                }
                tmp.vendorNativeType = native;
            }
            if (property["@sap:creatable"] && property["@sap:updatable"]) {
                if (property["@sap:creatable"] === "true" || property["@sap:updatable"] === "true") {
                    tmp.vendorReadOnly = false;
                } else {
                    tmp.vendorReadOnly = true;
                }
                tmp.createable = property["@sap:creatable"];
                tmp.updateable = property["@sap:updatable"];
            }

            if (property["@sap:filterable"]) {
                tmp.filterable = property["@sap:filterable"];
            }

            array.push(tmp);
        }
        return array;
    }

    function addSwaggerType(sapType, tmp) {
        let type = "";
        switch (sapType) {
            case "Edm.Decimal":
                type = "number";
                tmp.format = "double";
                break;
            case "Edm.Int32":
                type = "number";
                tmp.format = "int32";
                break;
            case "Edm.Boolean":
                type = "boolean";
                break;
            case "Edm.Binary":
                type = "string";
                tmp.format = "binary";
                break;
            case "c4codata.CLOSED_DatePeriod":
            case "c4codata.LOCALNORMALISED_DateTime":
            case "Edm.DateTimeOffset":
            case "Edm.DateTime":
                type = "date";
                // tmp.format = "date"; //prev datetime or type is date
                tmp.mask = "milliseconds";
                break;
            default: //Edm.String, Edm.Guid, c4codata.ENCRYPTED_LONG_Name,c4codata.MEDIUM_Description, c4codata.SHORT_Description, c4codata.Quantity, c4codata.Amount,
                type = "string";
        }
        tmp.type = type;
        return tmp;
    }

    function buildMetadata(entityTypes, objectList, objectName, prefix, objectsHitList) {
        let matchingEntity;
        for (let i = 0; i < entityTypes.length; i++) {
            let tmp = entityTypes[i];
            if (tmp["@Name"]) {
                let entity = tmp["@Name"];
                if (objectName.indexOf("Collection") > -1) {
                    objectName = objectName.replace("Collection", ""); //Trim off Collection if added in the request
                }
                //Identify the Object
                if (entity === objectName && !objectsHitList.includes(entity)) {
                    matchingEntity = tmp;
                    break;
                }
            }
        }

        if (matchingEntity == null) {
            return null;
        }

        objectsHitList = addToList(objectsHitList, matchingEntity["@Name"]);
        if (matchingEntity["Property"]) {
            objectList = objectList.concat(addFieldsToList(matchingEntity["Property"], prefix));
        }

        if (matchingEntity["NavigationProperty"]) {
            let navigationArray = matchingEntity["NavigationProperty"];
            for (let j = 0; j < navigationArray.length; j++) {
                let tmpMap = navigationArray[j];
                if (tmpMap["@Name"]) {
                    // let propertyObj = entityTypes.find(obj => obj["@Name"] === tmpMap["@Name"]);
                    let newPrefix = `${prefix}${tmpMap["@Name"]}[*].`;
                    let list = buildMetadata(entityTypes, objectList, tmpMap["@Name"], newPrefix, objectsHitList);
                    if (list) {
                        objectList = list;
                    }
                }
            }
        }
        return objectList;
    }
